import { EventEmitter } from 'events';

import log from '../log';
import * as rpc from '../xapi/rpc';


/**
 * @external {EventEmitter} https://nodejs.org/api/events.html#events_class_eventemitter
 */


/**
 * Backend abstract class.
 *
 * @extends {EventEmitter}
 * @interface
 *
 * @example <caption>Custom backend implementation</caption>
 * class MyBackend extends Backend {
 *   constructor(transport) {
 *     this._transport = transport.on('data', this._recvMsg.bind(this));
 *   }
 *
 *   _recvMsg(message) {
 *     const id = ... // determine request id
 *     const result = ... // process message
 *     this.onResult(id, result);
 *   }
 *
 *   // `command` is passed by the method handler, e.g. `xCommand()`.
 *   send(id, command) {
 *     const message = ... // use id and command to construct message
 *     this._transport.send(message);
 *   }
 *
 *   // this is dispatched by .execute()
 *   'xCommand()'(request, send) {
 *     const command = ... // do stuff with request
 *     return send(command).then(result => {
 *       // process result
 *     });
 *   }
 * }
 */
export default class Backend extends EventEmitter {
  constructor() {
    super();
    this.requests = {};
  }

  /**
   * Close the backend connection and free up resources. The backend should not
   * be used after it is closed and a new instance is required in order
   * re-initialize.
   */
  close() { // eslint-disable-line class-methods-use-this
  }

  /**
   * Promise that is resolved once the backend is ready to receive commands.
   *
   * @return {Promise} - Promised resolved when the backend is ready.
   */
  get isReady() { // eslint-disable-line class-methods-use-this
    return Promise.resolve(true);
  }

  /**
   * Default method handler. Called if there isn't a handler specified for the
   * method type. The default handler dies unless it is overridden in a sub-class.
   *
   * @param {Object} request - JSON-RPC request
   * @param {Function} send - Function for dispatching the request to the backend service.
   */
  defaultHandler({ method }, send) { // eslint-disable-line class-methods-use-this, no-unused-vars
    return Promise.reject(new Error(`Invalid request method: ${method}`));
  }

  /**
   * Determine the type of the JSON-RPC request. The type is used for
   * dispatching the request to the different rpc handlers. Sub-classes may
   * override this for custom routing behavior.
   *
   * @param {Object} request - JSON-RPC request
   * @return {string} - Request method type.
   */
  getRequestType({ method }) { // eslint-disable-line class-methods-use-this
    if (method.startsWith('xCommand')) {
      return 'xCommand';
    }
    return method;
  }

  /**
   * Transmit the given JSON-RPC payload to the backend service. The request
   * type is determined using {@link getRequestType} and the request is
   * delegated to method handlers, if they are defined for the given type. The
   * default handler ({@link defaultHandler}) is used if there is no handler
   * for the request type.
   *
   * Method handlers are defined on the sub-class, using the naming convention of
   * `<requestType>()` (notice the '()' suffix). Method handlers are passed the
   * request object and a `send` function to invoke for the request.
   *
   * @param {Object} request - JSON-RPC request to execute agains the backend service.
   * @return {Promise} - Promise resolved when response is received.
   */
  execute(request) {
    const { id } = request;
    const type = this.getRequestType(request);
    const handlerName = `${type}()`;
    const handler = typeof this[handlerName] === 'function'
      ? this[handlerName]
      : this.defaultHandler;

    return this.isReady
      .then(() => {
        const promise = new Promise((resolve) => {
          this.requests[id] = resolve;
        });
        const sender = (cmd, body) => {
          this.send(id, cmd, body);
          return promise;
        };
        log.debug('[backend] (request):', request);
        const result = handler.call(this, request, sender);
        return Promise.resolve(result);
      })
      .then((result) => {
        log.debug('[backend] (success):', result);
        this.emit('data', rpc.createResponse(id, result));
      })
      .catch((error) => {
        log.debug('[backend] (failure):', error);
        this.emit('data', rpc.createErrorResponse(id, error));
      });
  }

  /**
   * Called when receiving feedback from the backend service.
   *
   * @param {Object} result - JSON-RPC params data for the feedback event.
   */
  onFeedback(result) {
    this.emit('data', rpc.createRequest(null, 'xFeedback/Event', result));
  }

  /**
   * Called when the backend is done processing the response and ready to hand
   * it over to the XAPI frontend. The response should be a valid JSON-RPC
   * response.
   *
   * @param {string} id - Request id of the JSON-RPC request.
   * @param {Object} result - Result from the backend service.
   */
  onResult(id, result) {
    if (id) {
      const resolve = this.requests[id];
      delete this.requests[id];
      resolve(result);
    }
  }

  /**
   * Used to send the actual command to the backend service. The command
   * should be generated by the method handler and .
   *
   * @param {string} id - The request id.
   * @param {Array|Object|number|string} command - Command from method handler.
   * @abstract
   */
  send(id, command) { // eslint-disable-line class-methods-use-this, no-unused-vars
    throw new Error('Backend class must override .send()');
  }
}

import log from '../log';
import { JSONParser } from '../json-parser';
import * as rpc from '../xapi/rpc';

import Backend from './';


/**
 * @external {Duplex} https://nodejs.org/api/stream.html#stream_class_stream_duplex
 */


function formatValue(value) {
  switch (typeof value) {
    case 'boolean':
      return value ? 'True' : 'False';
    case 'number':
    case 'string':
      return JSON.stringify(value);
    default:
      throw new TypeError(`Invalid value ${JSON.stringify(value)}`);
  }
}


function paramString(key, value) {
  const values = Array.isArray(value) ? value : [value];
  return values
    .map(v => `${key}: ${formatValue(v)}`)
    .join(' ');
}


/**
 * Backend to communicate with a {@link Duplex} stream talking tshell (tsh).
 *
 * @extends {Backend}
 */
export default class TSHBackend extends Backend {
  /**
   * @param {Duplex} transport - Stream to interact with TSH.
   */
  constructor(transport) {
    super();

    this.feedbackQueries = {};
    this.nextFeedbackId = 0;
    this.parser = new JSONParser();
    this.requests = {};
    this.transport = transport;
    this.setState('idle');
    this.buffer = '';

    this.parser.on('data', this.onParserData.bind(this));
    this.parser.on('error', error => this.emit('error', error));

    Object.defineProperty(this, 'isReady', {
      configurable: false,
      enumerable: true,
      writable: false,
      value: new Promise((resolve, reject) => {
        if (this.state !== 'idle') {
          reject(new Error('TSHBackend is not in an idle state'));
          return;
        }

        this.connectResolve = resolve;
        this.setState('connecting');
      }),
    });

    this.transport
      .on('data', data => this.onTransportData(data))
      .on('error', error => this.emit('error', error))
      .on('close', () => {
        this.setState('closed');
        this.emit('close');
      });
  }

  bufferHasOK(buffer) {
    const lines = (this.buffer + buffer.toString()).split('\n');
    if (lines.length) {
      this.buffer = lines[lines.length - 1];
    }
    return lines.some(line => line === 'OK');
  }

  setState(newState) {
    this.state = newState;
  }

  /**
   * @override
   */
  close() {
    this.transport.close();
  }

  onParserData(data) {
    if (!{}.hasOwnProperty.call(data, 'ResultId')) {
      log.debug('[tsh] (feedback):', JSON.stringify(data));
      this.onFeedback(rpc.parseFeedbackResponse(data));
    } else {
      log.debug('[tsh] (result):', JSON.stringify(data));
      this.onResult(data.ResultId, data);
    }
  }

  onTransportData(data) {
    switch (this.state) {
      case 'connecting':
        if (this.bufferHasOK(data)) {
          log.debug('[transport] (connecting)', data.toString());
          this.setState('initializing');
          this.write('echo off\n');
          this.emit('initializing');
        }
        break;
      case 'initializing':
        if (this.bufferHasOK(data)) {
          log.debug('[transport] (initializing)', data.toString());
          this.buffer = '';
          this.write('xpreferences outputmode json\n');
          this.setState('ready');
          this.connectResolve(true);
          this.emit('ready');
        }
        break;
      case 'ready':
        log.debug(`to parser: "${data.toString()}"`);
        this.parser.write(data);
        break;
      default:
        this.emit('error', new Error('TSHBackend is in an invalid state for input'));
    }
  }

  /**
   * @override
   */
  send(id, command, body) {
    let cmd = `${command} | resultId="${id}"\n`;
    if (body !== undefined) {
      cmd += `${body}\n`;
      const length = cmd.length;
      cmd = `{${length}} \n${cmd}`;
    }

    this.write(cmd);
  }

  write(data) {
    log.debug(`write: ${JSON.stringify(data)}`);
    this.transport.write(data);
  }

  // XAPI json-rpc method handlers

  /**
   * @ignore
   */
  ['xCommand()']({ method, params }, send) { // eslint-disable-line class-methods-use-this
    const paramsCopy = Object.assign({}, params);
    const body = paramsCopy.body;
    delete paramsCopy.body;

    const tshParams = paramsCopy
      ? Object
        .keys(paramsCopy)
        .sort()
        .map(k => paramString(k, paramsCopy[k]))
      : [];

    const cmd = method
      .split('/')
      .concat(tshParams)
      .join(' ');

    return send(cmd, body)
      .then(rpc.createCommandResponse);
  }

  /**
   * @ignore
   */
  ['xFeedback/Subscribe()']({ params }, send) {
    const query = params.Query
      .map(part => (typeof part === 'number' ? `[${part}]` : `/${part}`))
      .join('');
    return send(`xfeedback register ${query}`)
      .then(() => {
        const id = this.nextFeedbackId;
        this.nextFeedbackId += 1;
        this.feedbackQueries[id] = query;
        return { Id: id };
      });
  }

  /**
   * @ignore
   */
  ['xFeedback/Unsubscribe()']({ params }, send) {
    const id = params.Id;

    if (!{}.hasOwnProperty.call(this.feedbackQueries, id)) {
      throw new Error(`Invalid feedback id: ${id}`);
    }

    const path = this.feedbackQueries[id];

    return send(`xfeedback deregister ${path}`)
      .then(() => {
        delete this.feedbackQueries[id];
        return true;
      });
  }

  /**
   * @ignore
   */
  ['xGet()'](request, send) { // eslint-disable-line class-methods-use-this
    const path = request.params.Path.join(' ');
    return send(`x${path}`)
      .then(response => rpc.createGetResponse(request, response));
  }

  /**
   * @ignore
   */
  ['xSet()'](request, send) { // eslint-disable-line class-methods-use-this
    const { params } = request;
    const path = params.Path.join(' ');
    const value = formatValue(params.Value);
    return send(`x${path}: ${value}`)
      .then(response => rpc.createSetResponse(request, response));
  }
}

import { EventEmitter } from 'events';


/**
 * @external {WebSocket} https://developer.mozilla.org/en-US/docs/Web/API/WebSocket
 */


/**
 * Backend to communicate with a WebSocket server.
 *
 * This backend expects to talk directly to a JSON-RPC WebSocket backend.
 * Authentication has to be handled by the transport layer, there is no
 * support for that on the socket itself.
 *
 * Once the socket is open, it is expected to be in the ready state.
 *
 * @implements {Backend}
 */
export default class WSBackend extends EventEmitter {
  /**
   * @param {Object|string} urlOrWS - WebSocket object or URL of the server.
   */
  constructor(urlOrWS) {
    super();
    /**
     * @type {WebSocket}
     */
    this.ws = typeof urlOrWS !== 'string' ? urlOrWS : new global.WebSocket(urlOrWS);
    this.ws.onclose = this.handleClose.bind(this);
    this.ws.onerror = this.handleError.bind(this);
    this.ws.onmessage = this.handleMessage.bind(this);

    let resolveReady;
    /**
     * @type {Promise}
     */
    this.isReady = new Promise((r) => { resolveReady = r; });
    this.ws.onopen = () => {
      this.emit('ready');
      resolveReady();
    };
  }

  close() {
    this.ws.close();
  }

  handleClose(event) {
    if (event.code !== 1000) {
      this.emit('error', 'WebSocket closed unexpectedly');
    } else {
      this.emit('close');
    }
  }

  handleError() {
    this.emit('error', 'WebSocket error');
  }

  handleMessage(message) {
    const data = JSON.parse(message.data);
    this.emit('data', data);
  }

  execute(command) {
    this.isReady.then(() => {
      this.ws.send(JSON.stringify(command));
    });
  }
}

#!/usr/bin/env node

import commander from 'commander';

import fs from 'fs';
import REPL from 'repl';

import pkg from '../package.json';
import log from './log';
import { connect } from './';


function evalFile(source, xapi) {
  const context = new Function('xapi', source); // eslint-disable-line no-new-func
  context(xapi);
}


function startRepl(xapi) {
  const repl = REPL.start({});
  repl.on('exit', () => xapi.close());
  repl.context.xapi = xapi;
}


commander
  .version(pkg.version)
  .arguments('<host> [file]')
  .description('connect to a codec and launch a repl')
  .option('-p, --port <port>', 'port to connect to', 22)
  .option('-U, --username <user>', 'username to authenticate with', 'admin')
  .option('-P, --password <password>', 'password to authenticate with', '')
  .option('-C, --command <command>', 'command to execute on remote host', '')
  .option(
    '-l, --loglevel <level>',
    'set application log level (trace|debug|info|warn|error|silent)',
    /^(trace|debug|info|warn|error|silent)$/i,
    'warn')
  .action((host, file, options) => {
    if (!host) {
      log.error('Please specify a host to connect to');
      commander.help();
    }

    const source = file && fs.readFileSync(file);
    const xapi = connect(host, options)
      .on('error', (error) => { log.error('xapi error:', error); })
      .on('ready', () => {
        if (source) {
          evalFile(source, xapi);
        } else {
          startRepl(xapi);
        }
      });
  })
  .parse(process.argv);

import parseUrl from 'url-parse';
import WebSocket from 'ws';

import log from './log';
import connectSSH from './transport/ssh';
import XAPI from './xapi';
import TSHBackend from './backend/tsh';
import WSBackend from './backend/ws';
import spawnTSH from './transport/tsh';


/**
 * Connect to an XAPI endpoint.
 *
 * @example
 * const xapi = connect('ssh://host.example.com:22');
 *
 * @param {string} url - Connection specification.
 * @param {Object} [options] - Connect options.
 * @param {string} [options.host] -
 *     Hostname to connect to.
 * @param {string} [options.username] -
 *     Username to authenticate with if protocol requires authentication.
 * @param {string} [options.password] -
 *     Password to authenticate with if protocol requires authentication.
 * @param {string} [options.loglevel] -
 *     Set the internal log level.
 * @return {XAPI} - XAPI interface connected to the given URI.
 */
export function connect(url, options) { // eslint-disable-line import/prefer-default-export
  if (arguments.length === 1 && typeof url === 'object') {
    /* eslint-disable no-param-reassign */
    options = url;
    url = '';
    /* eslint-enable */
  }

  const parsedUrl = parseUrl(url.match(/^\w+:\/\//) ? url : `ssh://${url}`);

  const opts = Object.assign({
    host: '',
    password: '',
    protocol: 'ssh:',
    username: 'admin',
    loglevel: 'warn',
  }, parsedUrl, options);

  const { hostname: host, port } = opts;
  delete opts.hostname;
  opts.host = host;

  log.setLevel(opts.loglevel);
  log.info('connecting to', url);

  let backend;
  switch (opts.protocol) {
    case '':
    case 'ssh:': {
      const transport = connectSSH(opts);
      backend = new TSHBackend(transport);
      break;
    }
    case 'tsh:': {
      const transport = spawnTSH(host, port);
      backend = new TSHBackend(transport);
      break;
    }
    case 'ws:':
    case 'wss:': {
      const transport = new WebSocket(url);
      backend = new WSBackend(transport);
      break;
    }
    default:
      throw new Error(`Invalid protocol: ${opts.protocol}`);
  }

  return new XAPI(backend);
}

import { Transform } from 'stream';

import Parser from 'jsonparse';


/**
 * @external {Transform} https://nodejs.org/api/stream.html#stream_class_stream_transform
 */


/**
 * Streaming JSON parser. Implements the Node.js {@link Duplex} stream API.
 */
export class JSONParser extends Transform {
  constructor() {
    super({ objectMode: true });
    this.enc = 'utf8';  // Default encoding
    this.reset();
  }

  reset() {
    this.parser = new Parser();
    this.parser.onError = this.onError.bind(this);
    this.parser.onValue = this.onValue.bind(this);
  }

  onError(e) {
    this.emit('error', e);
    this.reset();
  }

  onValue(value) {
    if (!this.parser.stack.length) {
      this.push(value);
    }
  }

  _flush(callback) {
    if (this.parser.stack.length) {
      this.onError(new Error('Unexpected end of input'));
    }
    callback();
  }

  _transform(chunk, encoding, callback) {
    const data = chunk.toString(this.enc);
    data.split(/\n/).forEach((line) => {
      try {
        this.parser.write(line);
      } catch (error) {
        this.onError(error);
      }
    });
    callback();
  }
}


/**
 * Synchronous frontend to {@link JSONparser}.
 *
 * @param {string} json - JSON string input.
 * @return {Object} - Parsed JSON object.
 */
export function parseJSON(json) {
  let obj;
  const parser = new JSONParser();

  parser.on('data', (_obj) => { obj = _obj; });
  parser.end(json);

  return obj;
}

/*
 * This module basically patches the `loglevel` library with some niceities for
 * named loggers. Named loggers prefix the log output with their log name and
 * their level can be set independently.
 */

import loglevel from 'loglevel';


if (!loglevel.isPatched) {
  const origMethodFactory = loglevel.methodFactory;
  const loggers = new Set();


  /*
   * Bless the `log` object with custom plugins
   */
  Object.assign(loglevel, {
    isPatched: true,

    methodFactory(methodName, logLevel, loggerName) {
      if (loggerName) {
        loggers.add(loggerName);
      }
      const rawMethod = origMethodFactory(methodName, logLevel, loggerName);
      return (...args) => {
        rawMethod(`[${loggerName || 'root'}]`, ...args);
      };
    },

    /*
     * Returns a list of logger names, excluding the root logger.
     */
    getLoggers() {
      return [...loggers];
    },

    setGlobalLevel(level) {
      const allLoggers = [loglevel].concat(
        loglevel.getLoggers().map(name => loglevel.getLogger(name)));

      allLoggers.forEach((logger) => { logger.setLevel(level); });
    },

    setLevelTrace() { loglevel.setGlobalLevel('trace'); },
    setLevelDebug() { loglevel.setGlobalLevel('debug'); },
    setLevelInfo() { loglevel.setGlobalLevel('info'); },
    setLevelWarn() { loglevel.setGlobalLevel('warn'); },
    setLevelError() { loglevel.setGlobalLevel('error'); },
  });


  // Required to apply the plugin to log
  loglevel.setLevel(loglevel.getLevel());
}


export default loglevel.getLogger('jsxapi');

import DuplexPassThrough from 'duplex-passthrough';
import { Client } from 'ssh2';

import log from '../log';


/*
 * Patch DuplexPassThrough.prototype.on to always return "this".
 * For some event types it delegates and return the return value of
 * the contained reader/writer instances.
 */
if (!DuplexPassThrough.prototype.on.isPatched) {
  const origOn = DuplexPassThrough.prototype.on;
  const patchedFunc = function on(...args) {
    origOn.call(this, ...args);
    return this;
  };
  patchedFunc.isPatched = true;
  DuplexPassThrough.prototype.on = patchedFunc;
  DuplexPassThrough.prototype.addListener = patchedFunc;
}


/**
 * Creates a {@link Duplex} SSH stream.
 *
 * @param {object} options
 * @param {string} options.host - Hostname or IP address.
 * @param {number} options.port - Port to connect to.
 * @param {string} options.username - Username used in authentication.
 * @param {string} options.password - Password used in authentication.
 * @param {string} options.command
 * - If command is specified, it is executed on the remote host instead of a login shell.
 * @return {Duplex} - SSH stream.
 */
export default function connectSSH(options) {
  let closing = false;

  const mergedOpts = Object.assign({
    client: new Client(),
    transport: new DuplexPassThrough(),
  }, options);

  const { client, password, transport } = mergedOpts;
  delete mergedOpts.password;

  function onKeyboardInteractive(n, i, il, p, finish) {
    finish([password]);
  }

  function onReady() {
    log.debug('[SSH] connection ready');
    client.shell(false, (err, sshStream) => {
      if (err) {
        log.error('[SSH] shell error:', err);
        transport.emit('error', err);
        return;
      }

      log.debug('[SSH] shell ready');
      sshStream
        .on('error', (error) => { transport.emit('error', error); })
        .on('end', () => {
          if (!closing) {
            transport.emit('error', 'Connection terminated remotely');
          }
        })
        .on('close', () => { transport.emit('close'); });

      if (options.command) {
        client.exec(options.command, (binaryErr, binaryStream) => {
          if (binaryErr) {
            log.error('[SSH] exec error:', err);
            transport.emit('error', binaryErr);
            return;
          }
          binaryStream.on('error', (error) => {
            log.error('[SSH] stream error:', error);
            transport.emit('error', error);
          });
          log.debug('[SSH] exec ready');
          transport.wrapStream(binaryStream);
        });
        return;
      }

      transport.wrapStream(sshStream);
    });
  }

  client
    .on('keyboard-interactive', onKeyboardInteractive)
    .on('ready', onReady)
    .on('error', (error) => { transport.emit('error', error.level); })
    .on('close', () => { transport.emit('close'); })
    .connect(Object.assign({ tryKeyboard: true }, mergedOpts));

  transport.close = () => {
    closing = true;
    client.end();
  };

  return transport;
}

import { Duplex } from 'stream';


/**
 * Duplex stream transport for integrations where JSXAPI does not have direct
 * access to the network or local ipc. E.g. for sandboxing or test stubs.
 */
export default class StreamTransport extends Duplex {
  /**
   * Creates a {@link Duplex} stream.
   *
   * @param {function(data: string)} send - Callback for outbound data
   * @return {Duplex} - Duplex stream.
   */
  constructor(send, options) {
    super(options);
    this.buffer = [];
    this.canPush = false;
    this.send = send;
    this.on('finish', () => { this.emit('close'); });
  }

  /**
   * Closes the stream transport
   */
  close() {
    this.end();
  }

  /**
   * @param {string} data - Push inbound data from the XAPI service to JSXAPI.
   * @return {boolean} - Boolean signaling if the stream can receive more data.
   */
  push(data) {
    this.buffer.push(data);
    return this.attemptFlush();
  }

  attemptFlush() {
    while (this.canPush && this.buffer.length) {
      this.canPush = super.push(this.buffer.shift());
    }
    return this.canPush;
  }

  _read() {
    this.canPush = true;
    this.attemptFlush();
  }

  _write(chunk, encoding, callback) {
    this.send(chunk, encoding, callback);
  }
}

import { spawn } from 'child_process';

import duplex from 'duplexer';


const TSH_BIN = 'tsh';


/**
 * Use the TSH binary to connect to a TSH server.
 *
 * @param {string} host - Host to connect to.
 * @param {number} port - Port to connect to.
 * @return {Promise<Duplex>} - TSH {@link Duplex} stream.
 */
export default function spawnTSH(host, port) {
  const child = spawn(TSH_BIN, ['--port', port]);
  const stream = duplex(child.stdin, child.stdout);
  stream.close = () => child.kill();
  return stream;
}

import normalizePath from './normalizePath';
import { Listenable, Gettable, Settable, mix } from './mixins';


/**
 * Common base class for XAPI section types (commands, configs, events, statuses).
 *
 * @interface
 */
class Component {
  /**
   * Prefix to add to all paths for the component.
   *
   * @type {string}
   */
  prefix = ''

  /**
   * @param {XAPI} xapi - XAPI instance.
   */
  constructor(xapi) {
    /**
     * @type {XAPI}
     */
    this.xapi = xapi;
  }

  /**
   * Normalizes a path including the component prefix.
   *
   * @param {Array|string} path - Normalize an XAPI path.
   * @return {Array} - Normalized path.
   */
  normalizePath(path) {
    const normalized = normalizePath(path);
    const { prefix } = this;
    return !prefix ? normalized : [prefix].concat(normalized);
  }
}


/**
 * Interface to XAPI configurations.
 *
 * @extends {Component}
 * @extends {Listenable}
 * @extends {Gettable}
 * @extends {Settable}
 */
export class Config extends mix(Component, Listenable, Gettable, Settable) {
  prefix = 'Configuration'
}


/**
 * Interface to XAPI events.
 *
 * @extends {Component}
 * @extends {Listenable}
 */
export class Event extends mix(Component, Listenable) {
  prefix = 'Event'
}


/**
 * Interface to XAPI statuses.
 *
 * @extends {Component}
 * @extends {Listenable}
 * @extends {Gettable}
 */
export class Status extends mix(Component, Listenable, Gettable) {
  prefix = 'Status'
}

export const UNKNOWN_ERROR = 0;
export const COMMAND_ERROR = 1;
export const ILLEGAL_VALUE = 2;
export const INVALID_PATH = 3;
export const PARAMETER_ERROR = 4;
export const INVALID_RESPONSE = 5;
export const INVALID_STATUS = 6;
export const METHOD_NOT_FOUND = -32601;


export class XAPIError extends Error {
  constructor(code, reason, data) {
    if (typeof reason !== 'string') {
      throw new Error('Reason for XAPIError must be a string');
    }

    if (typeof code !== 'number') {
      throw new Error('Error code for XAPIError must be a number');
    }

    super(reason);
    this.code = code;
    if (data !== undefined) {
      this.data = data;
    }
  }
}


export class IllegalValueError extends XAPIError {
  constructor(reason) {
    super(ILLEGAL_VALUE, reason);
  }
}


export class InvalidPathError extends XAPIError {
  constructor(reason, xpath) {
    super(INVALID_PATH, reason);
    this.data = { xpath };
  }
}


export class ParameterError extends XAPIError {
  constructor() {
    super(PARAMETER_ERROR, 'Invalid or missing parameters');
  }
}

import { EventEmitter } from 'events';

import log from '../log';

import normalizePath from './normalizePath';

/**
 * Group feedback deregister handlers for bookkeeping.
 */
export class FeedbackGroup {
  constructor(handlers) {
    this.handlers = handlers;
  }

  /**
   * Add a deregister handler function to the feedback group.
   *
   * @param {function()} handler - Handler to add to the group.
   * @return {FeedbackGroup} - this for chaining.
   */
  add(handler) {
    this.handlers.push(handler);
    return this;
  }

  /**
   * Remove a deregister handler function from the feedback group.
   *
   * @param {function()} handler - Handler to remove from the group.
   * @return {FeedbackGroup} - this for chaining.
   */
  remove(handler) {
    this.handlers = this.handlers.filter(h => h !== handler);
    return this;
  }

  /**
   * Call the deregister handler functions associated with this group.
   *
   * @return {FeedbackGroup} - this for chaining.
   */
  off() {
    this.handlers.forEach((handler) => {
      handler();
    });
    this.handlers = [];
    return this;
  }
}

function defaultInterceptor(payload, emit) {
  emit(payload);
}

function dispatch(feedback, data, root = data, path = []) {
  if (Array.isArray(data)) {
    data.forEach((child) => {
      dispatch(feedback, child, root, path);
      dispatch(feedback, child, root, path.concat(child.id));
    });
    return;
  }

  const emitPath = path.join('/').toLowerCase();
  feedback.eventEmitter.emit(emitPath, data, root);

  if (typeof data === 'object') {
    Object.keys(data).forEach((key) => {
      dispatch(feedback, data[key], root, path.concat(key));
    });
  }
}

/**
 * Feedback handler for the XAPI.
 *
 * @example <caption>Register a feedback listener</caption>
 * xapi.feedback.on('Status/Audio/Volume', data => {
 *   console.log(`Received feedback data: ${data}`);
 * });
 *
 * @example <caption>Get the feedback root payload</caption>
 * xapi.feedback.on('Status/Audio/Volume', (data, payload) => {
 *   console.log(`System volume changed to: ${data}`);
 *   JSON.stringify(payload) // => { Status: { Audio: { Volume: data } } }
 * });
 *
 * @example <caption>Listen to array elements</caption>
 * xapi.feedback.on('Status/Call[42]/Status', callStatus => {
 *   console.log(`Call status for call number 42 is: ${callStatus}`);
 * });
 *
 * @example <caption>Bundle feedback listeners for easy unsubscription</caption>
 * const feedbackGroup = xapi.feedback.group([
 *   xapi.status.on('Audio/Volume', volumeListener),
 *   xapi.status.on('Call', callListener),
 * ]);
 *
 * // Disable feedback listening for all listeners of the group.
 * feedbackGroup.off();
 *
 * @example <caption>Register listener with Array path</caption>
 * const off = xapi.feedback.on('Status/Audio/Volume', listener);
 * off(); // De-register feedback
 */
export default class Feedback {
  /**
   * @param {XAPI} xapi - XAPI instance.
   * @param {function} interceptor - Feedback interceptor.
   */
  constructor(xapi, interceptor = defaultInterceptor) {
    Object.defineProperties(this, {
      eventEmitter: { value: new EventEmitter() },
      interceptor: { value: interceptor },
      xapi: { value: xapi },
      subscriptions: { value: [], writable: true },
    });
  }

  /**
   * Registers a feedback listener with the backend service which is invoked
   * when there is feedback matching the subscription query.
   *
   * @param {Array|string} path - Path to subscribe to
   * @param {function} listener - Listener invoked on feedback
   */
  on(path, listener) {
    log.info(`new feedback listener on: ${path}`);
    const eventPath = normalizePath(path)
      .join('/')
      .toLowerCase();

    this.eventEmitter.on(eventPath, listener);

    const registration = this.xapi.execute('xFeedback/Subscribe', {
      Query: normalizePath(path),
    });

    const off = () => {
      registration.then(({ Id }) => {
        this.xapi.execute('xFeedback/Unsubscribe', { Id });
      });

      this.eventEmitter.removeListener(eventPath, listener);
    };

    return off;
  }

  /**
   * Registers a feedback listener similar to {@link on}, but the subscription
   * is removed after the first invocation of the listener.
   *
   * @param {Array|string} path - Path to subscribe to
   * @param {function} listener - Listener invoked on feedback
   */
  once(path, listener) {
    let off;
    const wrapped = (...args) => {
      if (typeof off === 'function') off();
      listener.call(this, ...args);
    };
    wrapped.listener = listener;
    off = this.on(path, wrapped);
    return off;
  }

  /**
   * Remove feedback registration.
   *
   * @deprecated use deactivation handler from `.on()` and `.once()` instead.
   */
  // eslint-disable-next-line class-methods-use-this
  off() {
    throw new Error(
      '.off() is deprecated. Use return value deactivate handler from .on() instead.',
    );
  }

  /**
   * Dispatches feedback data to the registered handlers.
   *
   * @param {Object} data - JSON data structure of feedback data.
   * @return {FeedbackHandler} - Returns self for chaining.
   */
  dispatch(data) {
    this.interceptor(data, (d = data) => dispatch(this, d));
    return this;
  }

  /**
   * Creates a grouper object which tracks which tracks the feedback paths and
   * listeners being added to it.
   *
   * @return {FeedbackGroup} - Proxy object for xapi.feedback
   *
   * @example <caption>Bundle feedback listeners for easy unsubscription</caption>
   * // Create a group
   * const group = xapi.feedback.group([
   *   xapi.status.on('Audio Volume', (volume) => {
   *     // ...
   *   }),
   *   xapi.config.on('Audio DefaultVolume', (volume) => {
   *     // ...
   *   }),
   * ]);
   *
   * const handler = xapi.status.on('Call', (call) => { ... });
   *
   * // Add handler to the group
   * group.add(handler);
   *
   * // Remove handler from the group
   * group.remove(handler);
   *
   * // Unregister from all feedback handlers
   * group.off();
   */
  // eslint-disable-next-line class-methods-use-this
  group(handlers) {
    return new FeedbackGroup(handlers);
  }
}

import { EventEmitter } from 'events';

import log from '../log';
import * as rpc from './rpc';
import normalizePath from './normalizePath';

import Feedback from './feedback';
import { Config, Event, Status } from './components';


/**
 * User-facing API towards the XAPI. Requires a backend for communicating
 * with an XAPI instance. It should be possible to write backends for all kinds
 * of transports (TSH over SSH, Websockets, HTTP, plain sockets, etc.)
 *
 * @example <caption>Initialization</caption>
 * const xapi = new XAPI(backend);
 *
 * @example <caption>Invoke commands</caption>
 * xapi
 *   .command('Dial', { Number: 'johndoe@example.com' })
 *   .then(onSuccess, onFailure);
 *
 * @example <caption>Fetch a status</caption>
 * xapi
 *   .status.get('Audio Volume')
 *   .then((value) => { console.log(value); });
 *
 * @example <caption>Set a configuration</caption>
 * xapi
 *   .config.set('Audio DefaultVolume', 100);
 *
 * @example <caption>Listen to an event</caption>
 * xapi.event.on('Some Event Name', (data) => {
 *   console.log(`Received feedback data: ${data}`);
 * });
 */
export default class XAPI extends EventEmitter {
  /**
   * @param {Backend} backend - Backend connected to an XAPI instance.
   * @param {object} options - XAPI object options.
   * @param {function} options.feedbackInterceptor - Feedback interceptor.
   */
  constructor(backend, options = {}) {
    super();

    /** @type {Backend} */
    this.backend = backend;

    /** @ignore */
    this.requestId = 1;

    /** @ignore */
    this.requests = {};

    /**
     * Interface to XAPI feedback registration.
     * @type {Feedback}
     */
    this.feedback = new Feedback(this, options.feedbackInterceptor);

    /**
     * Interface to XAPI configurations.
     * @type {Config}
     */
    this.config = new Config(this);

    /**
     * Interface to XAPI events.
     * @type {Event}
     */
    this.event = new Event(this);

    /**
     * Interface to XAPI statuses.
     * @type {Status}
     */
    this.status = new Status(this);

    // Restrict object mutation
    Object.defineProperties(this, {
      config: { writable: false },
      event: { writable: false },
      feedback: { writable: false },
      status: { writable: false },
    });
    Object.seal(this);

    backend
      .on('close', () => { this.emit('close'); })
      .on('error', (error) => { this.emit('error', error); })
      .on('ready', () => { this.emit('ready'); })
      .on('data', this.handleResponse.bind(this));
  }

  /**
   * Close the XAPI connection.
   *
   * @return {XAPI} - XAPI instance..
   */
  close() {
    this.backend.close();
    return this;
  }

  /**
   * Executes the command specified by the given path.
   *
   * @example
   * // Space delimited
   * xapi.command('Presentation Start');
   *
   * // Slash delimited
   * xapi.command('Presentation/Start');
   *
   * // Array path
   * xapi.command(['Presentation', 'Start']);
   *
   * // With parameters
   * xapi.command('Presentation Start', { PresentationSource: 1 });
   *
   * // Multi-line
   * xapi.command('UserInterface Extensions Set', { ConfigId: 'example' }, `
   *  <Extensions>
   *    <Version>1.1</Version>
   *    <Panel item="1" maxOccurrence="n">
   *      <Icon>Lightbulb</Icon>
   *      <Type>Statusbar</Type>
   *      <Page item="1" maxOccurrence="n">
   *        <Name>Foo</Name>
   *        <Row item="1" maxOccurrence="n">
   *          <Name>Bar</Name>
   *          <Widget item="1" maxOccurrence="n">
   *            <WidgetId>widget_3</WidgetId>
   *            <Type>ToggleButton</Type>
   *          </Widget>
   *        </Row>
   *      </Page>
   *    </Panel>
   *  </Extensions>
   * `);
   *
   * @param {Array|string} path - Path to command node.
   * @param {Object} [params] - Object containing named command arguments.
   * @param {string} [body] - Multi-line body for commands requiring it.
   * @return {Promise} - Resolved with the command response when ready.
   */
  command(path, params, body) {
    const apiPath = normalizePath(path).join('/');
    const method = `xCommand/${apiPath}`;
    const executeParams = body === undefined ? params : Object.assign({ body }, params);
    return this.execute(method, executeParams);
  }

  /** @private */
  handleResponse(response) {
    const { id, method } = response;
    if (method === 'xFeedback/Event') {
      log.debug('feedback:', response);
      this.feedback.dispatch(response.params);
    } else {
      if ({}.hasOwnProperty.call(response, 'result')) {
        log.debug('result:', response);
        const { resolve } = this.requests[id];
        resolve(response.result);
      } else {
        log.debug('error:', response);
        const { reject } = this.requests[id];
        reject(response.error);
      }
      delete this.requests[id];
    }
  }

  /** @private */
  nextRequestId() {
    const requestId = this.requestId;
    this.requestId += 1;
    return requestId.toString();
  }

  /**
   * Execute the given JSON-RPC request on the backend.
   *
   * @example
   * xapi.execute('xFeedback/Subscribe', {
   *   Query: ['Status', 'Audio'],
   * });
   *
   * @param {String} method - Name of RPC method to invoke.
   * @param {Object} [params] - Parameters to add to the request.
   * @return {Promise} - Resolved with the command response.
   */
  execute(method, params) {
    return new Promise((resolve, reject) => {
      const id = this.nextRequestId();
      const request = rpc.createRequest(id, method, params);
      this.backend.execute(request);
      this.requests[id] = { resolve, reject };
    });
  }
}

