/**
 * Mixin for XAPI sections that can trigger feedback.
 *
 * @interface
 */
export class Listenable {
  /**
   * Register a new listener on the given path.
   *
   * @param {string} path - Path to XAPI entry.
   * @param {function(data: Object): null} listener - Callback handler called on changes.
   * @return {function()} - Handler to deregister the feedback registration.
   */
  on(path, listener) {
    return this.xapi.feedback.on(this.normalizePath(path), listener);
  }

  /**
   * Register a new listener on the given path, de-register
   * after the first change happened.
   *
   * @param {string} path - Path to XAPI entry.
   * @param {function(data: Object): null} listener - Callback handler called on changes.
   * @return {Object} - Handler to deregister the feedback registration.
   */
  once(path, listener) {
    return this.xapi.feedback.once(this.normalizePath(path), listener);
  }


  /**
   * De-register the given listener on the given path.
   *
   * @deprecated use deactivation handler from `.on()` and `.once()` instead.
   */
  off() {
    this.xapi.feedback.off();
  }
}


/**
 * Mixin for XAPI sections that can hold a value that may be fetched.
 *
 * @interface
 */
export class Gettable {
  /**
   * Gets the value of the given path.
   *
   * @example
   * xapi.status
   *   .get('Audio Volume')
   *   .then((volume) => { console.log(volume); });
   *
   * @example
   * xapi.config
   *   .get('Audio DefaultVolume')
   *   .then((volume) => { console.log(volume); });
   *
   * @param {string} path - Path to configuration node.
   * @return {Promise} - Resolved to the configuration value when ready.
   */
  get(path) {
    return this.xapi.execute('xGet', {
      Path: this.normalizePath(path),
    });
  }
}


/**
 * Mixin for XAPI sections that can hold a value that may be fetched.
 *
 * @interface
 */
export class Settable {
  /**
   * Sets the path to the given value.
   *
   * @example
   * xapi
   *   .config.set('SystemUnit Name', 'My System');
   *
   * @param {string} path - Path to status node.
   * @param {number|string} value - Configuration value.
   * @return {Promise} - Resolved to the status value when ready.
   */
  set(path, value) {
    return this.xapi.execute('xSet', {
      Path: this.normalizePath(path),
      Value: value,
    });
  }
}


/**
 * Extend {Base} class and apply {Mixins}.
 *
 * @param {Object} Base - Base class to extend.
 * @param {Array} Mixins - Mixins to apply.
 * @return Object - New ad-hoc base class with mixins applied.
 */
export function mix(Base, ...Mixins) {
  class Class extends Base {}

  Mixins.forEach((mixin) => {
    Object
      .getOwnPropertyNames(mixin.prototype)
      .map(key => [key, mixin.prototype[key]])
      .filter(([, v]) => typeof v === 'function' && v !== 'constructor')
      .forEach(([name, method]) => {
        Class.prototype[name] = method;
      });
  });

  return Class;
}

/**
 * Normalizes a path by turning it into an Array of strings.
 * Removes empty parts of the path and ignores redundant whitespace or
 * slashes. Each path element is also capitalized.
 *
 * @param {Array|string} path - Array or string path to normalize.
 * @return {Array} - Array of path segments.
 */
export default function normalizePath(path) {
  const split = Array.isArray(path) ? path : path.match(/(\w+)/g);
  return !split ? [] : split
    .map((element) => {
      if (/^\d+$/.test(element)) {
        return parseInt(element, 10);
      }
      return element.charAt(0).toUpperCase() + element.slice(1);
    });
}

import {
  IllegalValueError,
  InvalidPathError,
  ParameterError,
  XAPIError,
  UNKNOWN_ERROR,
  INVALID_RESPONSE,
  INVALID_STATUS,
} from './exc';

const scalars = ['number', 'string'];
const isScalar = value => scalars.indexOf(typeof value) >= 0;


const VERSION = '2.0';


/*
 * Collapse "Value" into parent + skip lowercase props
 */
export function collapse(data) {
  if (Array.isArray(data)) {
    return data.map(collapse);
  } else if (isScalar(data)) {
    return data;
  } else if ({}.hasOwnProperty.call(data, 'Value') && isScalar(data.Value)) {
    return data.Value;
  }

  const result = {};

  Object.keys(data).forEach((key) => {
    result[key] = collapse(data[key]);
  });

  return result;
}


export function createRequest(id, method, params) {
  const request = { jsonrpc: VERSION, method };

  if (id) {
    request.id = id;
  }

  if (params) {
    request.params = {};

    Object.keys(params).forEach((key) => {
      const value = params[key];

      if (key !== 'body' && typeof value === 'string' && value.indexOf('\n') !== -1) {
        throw new Error('Parameters may not contain newline characters');
      }

      request.params[key] = params[key];
    });
  }

  return request;
}


export function createResponse(id, result) {
  return { jsonrpc: VERSION, id, result };
}


export function createErrorResponse(id, error) {
  let payload;

  if (error instanceof XAPIError) {
    payload = {
      code: error.code,
      message: error.message,
    };
  } else {
    payload = {
      code: UNKNOWN_ERROR,
      message: (error.message || error).toString(),
    };
  }

  if ({}.hasOwnProperty.call(error, 'data')) {
    payload.data = error.data;
  }

  return { jsonrpc: VERSION, id, error: payload };
}


export function parseFeedbackResponse(response) {
  return collapse(response);
}


function assertValidCommandResponse(response) {
  if (!{}.hasOwnProperty.call(response, 'CommandResponse')) {
    throw new XAPIError(
      INVALID_RESPONSE, 'Invalid command response: Missing "CommandResponse" attribute');
  }

  const keys = Object.keys(response.CommandResponse);
  if (keys.length !== 1) {
    throw new XAPIError(
      INVALID_RESPONSE, `Invalid command response: Wrong number of keys (${keys.length})`);
  }

  const root = response.CommandResponse[keys[0]];

  switch (root.status) {
    case 'Error': {
      const body = collapse(root);
      const { Error, Reason, XPath } = body;
      if (XPath) {
        throw new InvalidPathError(Reason, XPath);
      }
      const reason = Error || Reason || keys[0];
      throw new XAPIError(UNKNOWN_ERROR, reason, body);
    }
    case 'ParameterError':
      throw new ParameterError();
    case 'OK':
      return root;
    default:
      throw new XAPIError(INVALID_STATUS, `Invalid command status: ${root.status}`);
  }
}


export function createCommandResponse(response) {
  const root = assertValidCommandResponse(response);
  const collapsed = collapse(root);
  return Object.keys(collapsed).length ? collapsed : null;
}


function digObj(path, obj) {
  const parts = path.slice();
  let value = obj;

  while (parts.length) {
    const part = parts.shift();
    if (Array.isArray(value)) {
      value = value.find(v => parseInt(v.id, 10) === part);
    } else if (!{}.hasOwnProperty.call(value, part)) {
      return undefined;
    } else {
      value = value[part];
    }
  }

  return value;
}


export function createGetResponse(request, response) {
  if ({}.hasOwnProperty.call(response, 'CommandResponse')) {
    assertValidCommandResponse(response);
  }

  return digObj(request.params.Path, collapse(response));
}


export function createSetResponse(request, response) {
  if (Object.keys(response).length > 1) {
    const leaf = digObj(request.params.Path, response);
    if (leaf.error === 'True') {
      throw new IllegalValueError(leaf.Value);
    }
  }
  return null;
}

# JSXAPI

[![Build Status](https://travis-ci.org/cisco-ce/jsxapi.svg?branch=master)](https://travis-ci.org/cisco-ce/jsxapi)

A set of tools to integrate with the Cisco Telepresence Endpoint APIs in
JavaScript.

## Quick start example, using SSH

```javascript
const jsxapi = require('jsxapi');

// Connect over ssh to a codec
const xapi = jsxapi.connect('ssh://host.example.com', {
  username: 'admin',
  password: 'password',
});

// Handle errors
xapi.on('error', (err) => {
  // !! Note of caution: This event might fire more than once !!
  console.error(`xapi error: ${err}`);
});

// Set up a call
xapi.command('Dial', { Number: 'user@example.com' });

// Fetch volume and print it
xapi.status
  .get('Audio Volume')
  .then((volume) => { console.log(volume); });

// Set a configuration
xapi.config.set('SystemUnit Name', 'My System');

// Listen to feedback
const off = xapi.event.on('Standby', (event) => {
  // ...
});

// De-register feedback
off();
```

## Documentation

The full API documentation can be built by running `npm install` in a `jsxapi`
module directory. Documentation will be located under `docs/` can then be opened
in a browser.

More specifically:

```
mkdir tmp
cd tmp
npm install jsxapi
cd node_modules/jsxapi
npm install
```

Then open `./docs/index.html`.

